query {
   users {
      id,
      firstName
   }
   profiles {
      id,
      birthday,
      memberTypeId,
   }
   posts {
      id,
      title
   }
   memberTypes {
      id,
      monthPostsLimit
   }
}

query($userId: ID,  $profileId: ID, $postId: ID, $memberTypeId: String) {
    user(id: $userId) {
        id,
        lastName
    }
    profile(id: $profileId) {
        id,
        birthday,
    }
    post(id: $postId) {
        title,
        content,
    }
    memberType(id: $memberTypeId) {
        discount,
        monthPostsLimit
    }
}

